mapper.update:
UPDATE lesson


      lesson_name = #{lessonName},


      lesson_introduced = #{lessonIntroduced},


      reward_star = #{rewardStar},

    update_at = #{updateAt}

where id = #{id}

mapper.select:
SELECT *
FROM lesson


        and subject_name = #{subjectName}


        and course_level = #{courseLevel}


        and course_name = #{courseName}


        and lesson_status = #{lessonStatus}


        and lesson_name like concat(concat('%',#{lessonName},'%'))


order by update_at desc

SELECT id
from lesson
WHERE subject_id = #{subjectId} and course_id = #{courseId} and lesson_name = #{lessonName}

mapper.update:
UPDATE lesson
SET lesson_status = #{lessonStatus}, update_at = #{updateAt}
where id = #{id}

mapper.select:
select lesson_name
from lesson
where course_id = #{courseId}

select *
from student_lesson_datum
where student_id = #{studentId} and class_id = #{classId}

mapper.update:
update student_lesson_datum


    buy = #{buy},


        enshrine = #{enshrine},



      datum = #{datum},


        lesson_status = #{lessonStatus}


where student_id = #{studentId} and class_id = #{classId}
